import { Component } from 'react'
import './index.css'

const viewConstent = {
    sedOtpView: 'SENDOTPVIEW',
    verifyOtpView: 'VERIFYOTPVIEW',
    loadingView: 'LOADING',
    successView: 'SUCCESS',
}

class CreateAccount extends Component {

    state = {
        createAccountView: viewConstent.sedOtpView,
        email: '',
        otp: '',
        password: '',
        errorMsg: 'Error'
    }

    onChangeNewEmail = event => {
        this.setState({email: event.target.value})
    }

    changeRole = event => {
        this.setState({role: event.target.value})
    }

    sendOtpView = () => {
        const {email, errorMsg} = this.state
        return (
            <form className='login-form' onSubmit={this.sendOtp}>
                <div className='input-container'>
                    <p className='input-label-text'>
                        Enter you Email:
                    </p>
                    <input className='input-box' type='text' placeholder='email' onChange={this.onChangeNewEmail} value={email}/>
                </div>
                <div className='input-container'>
                    <p className='input-label-text'>
                        Choose Your Role:
                    </p>
                    <select className='input-box' onChange={this.changeRole}>
                        <option vlaue='Admin'>Admin</option>
                        <option vlaue='Patient' selected>Patient</option>
                        <option vlaue='Doctor'>Doctor</option>
                        <option vlaue='Staff'>Staff</option>
                    </select>
                    <button className='send-otp-btn' type='submit'>Send OTP</button>
                    {errorMsg.length === 0 ? null : <p className='error-msg'>{errorMsg}</p>}
                </div>
            </form>
        )
    }

    renderRespectiveCreateAccountView = () => {
        const {createAccountView} = this.state
        switch(createAccountView){
            case viewConstent.sedOtpView:
                return this.sendOtpView()
            case viewConstent.verifyOtpView:
                return <h1>verifyOtpView</h1>
            case viewConstent.loadingView:
                return <h1>LOADING</h1>
            case viewConstent.successView:
                return <h1>SUCCESS</h1>
            default:
                return null
        }
    }

    render() {
        return (
            <div className='create-account-bg'>
                {this.renderRespectiveCreateAccountView()}
            </div>
        )
    }
}

export default CreateAccount
